function c(t,o,e=document){const n=e.querySelector(t);if(!(n instanceof o))throw new Error(`Element is not of type ${o.name}: ${t}`);return n}for(const t of document.querySelectorAll("[data-code-block]")){const o=c("[data-code]",HTMLElement,t),e=c("[data-copy-button]",HTMLButtonElement,t);e.addEventListener("click",()=>{navigator.clipboard.writeText(o.innerText),e.innerText="Copied",e.classList.add("copy-button-active"),window.setTimeout(()=>{e.innerText="Copy",e.classList.remove("copy-button-active")},2e3)})}
